import React from 'react';

function DescriptionSection({ showDescription, toggleText }) {
  return (
    <section>
      <h2>Descripción del lugar</h2>
      <div className={showDescription ? 'visible' : 'hidden'} id="long-text">
        <p>
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Expedita harum quaerat corrupti porro maxime id
          ipsam vero odio pariatur, non veniam ducimus laborum obcaecati inventore rem, alias nihil cum autem! Lorem
          ipsum dolor sit amet consectetur adipisicing elit. Alias earum ullam quam porro sed tempore, error aliquam
          modi exercitationem voluptatibus, eum suscipit quos nam accusamus, dolor pariatur quasi. Ut, aperiam. Lorem
          ipsum dolor sit amet consectetur adipisicing elit. Et illum distinctio placeat velit vel repudiandae quae
          consequuntur quaerat, adipisci sint, consequatur dolorem perspiciatis veniam quo architecto. Voluptates labore
          temporibus corrupti! Lorem, ipsum dolor sit amet consectetur adipisicing elit. Explicabo, debitis repellendus
          dolore voluptates beatae repudiandae facilis velit saepe, quas laborum reprehenderit harum eius quidem magni
          illum non, sequi aut neque.
        </p>
        <br />
      </div>
      <a id="toggle-link" className="show-more" onClick={toggleText}>
        {showDescription ? 'Ocultar descripcion completa' : 'Ver descripcion completa'}
      </a>
      <hr />
      <br />

      {/* ... Resto del contenido de la sección de descripción */}
    </section>

);
}

export default DescriptionSection;
